document.querySelectorAll("[gradient-border]").forEach(t=>{let e=t.getAttribute("gradient-border");if(e){let r=(e=e.split(",")).shift(),a=e.toString().trim(),n=getComputedStyle(t).borderRadius,o=parseInt(n.substr(0,n.length-2)),i=parseInt(r.substr(0,r.length-2)),p=o-i+1,l=o-i-1;t.style.border=`${r} solid transparent`,t.style.background=`linear-gradient(to right, ${a}) border-box`,mask=`\n                radial-gradient(circle at bottom left ,transparent ${l}px,#fff ${p}px) top right/${n} ${n} no-repeat,\n                radial-gradient(circle at top right   ,transparent ${l}px,#fff ${p}px) bottom left/${n} ${n} no-repeat,\n                radial-gradient(circle at top left    ,transparent ${l}px,#fff ${p}px) bottom right/${n} ${n} no-repeat,\n                radial-gradient(circle at bottom right,transparent ${l}px,#fff ${p}px) top left/${n} ${n} no-repeat,\n                linear-gradient(to bottom,#fff ${r},transparent ${r}) top   /100% 50% no-repeat,\n                linear-gradient(to top   ,#fff ${r},transparent ${r}) bottom/100% 50% no-repeat,\n                linear-gradient(to right ,#fff ${r},transparent ${r}) left  /50% 100% no-repeat,\n                linear-gradient(to left  ,#fff ${r},transparent ${r}) right /50% 100% no-repeat\n            `.replace(/\n/g," "),t.setAttribute("mask",mask);let f=t.getAttribute("style");f+=`-webkit-mask: ${mask}`,t.setAttribute("style",f)}});